#!/bin/bash
# Uninstall script for RStudio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling RStudio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping RStudio if running..."
pkill -f "RStudio" 2>/dev/null || true

# Remove /Applications/RStudio.app
echo "Removing /Applications/RStudio.app..."
if [ -d "/Applications/RStudio.app" ]; then
    rm -rf "/Applications/RStudio.app" 2>/dev/null || true
elif [ -f "/Applications/RStudio.app" ]; then
    rm -f "/Applications/RStudio.app" 2>/dev/null || true
fi

# Remove $HOME/.rstudio-desktop
echo "Removing $HOME/.rstudio-desktop..."
if [ -d "$HOME/.rstudio-desktop" ]; then
    rm -rf "$HOME/.rstudio-desktop" 2>/dev/null || true
elif [ -f "$HOME/.rstudio-desktop" ]; then
    rm -f "$HOME/.rstudio-desktop" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
