echo "Removing /Applications/Shottr.app..."
if [ -d "/Applications/Shottr.app" ]; then
    rm -rf "/Applications/Shottr.app" 2>/dev/null || true
elif [ -f "/Applications/Shottr.app" ]; then
    rm -f "/Applications/Shottr.app" 2>/dev/null || true
fi

