#!/bin/bash
# Uninstall script for Signal
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Signal..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Signal if running..."
pkill -f "Signal" 2>/dev/null || true

# Remove /Applications/Signal.app
echo "Removing /Applications/Signal.app..."
if [ -d "/Applications/Signal.app" ]; then
    rm -rf "/Applications/Signal.app" 2>/dev/null || true
elif [ -f "/Applications/Signal.app" ]; then
    rm -f "/Applications/Signal.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Signal
echo "Removing $HOME/Library/Application Support/Signal..."
if [ -d "$HOME/Library/Application Support/Signal" ]; then
    rm -rf "$HOME/Library/Application Support/Signal" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Signal" ]; then
    rm -f "$HOME/Library/Application Support/Signal" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist
echo "Removing $HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist..."
if [ -d "$HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.whispersystems.signal-desktop.helper.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.whispersystems.signal-desktop.plist
echo "Removing $HOME/Library/Preferences/org.whispersystems.signal-desktop.plist..."
