elif [ -f "$HOME/Library/Preferences/org.whispersystems.signal-desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.whispersystems.signal-desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.whispersystems.signal-desktop.savedState
echo "Removing $HOME/Library/Saved Application State/org.whispersystems.signal-desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.whispersystems.signal-desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.whispersystems.signal-desktop.savedState" 2>/dev/null || true
