#!/bin/bash
# Uninstall script for Telegram for macOS
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Telegram for macOS..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Telegram for macOS if running..."
pkill -f "Telegram for macOS" 2>/dev/null || true

# Remove /Applications/Telegram.app
echo "Removing /Applications/Telegram.app..."
if [ -d "/Applications/Telegram.app" ]; then
    rm -rf "/Applications/Telegram.app" 2>/dev/null || true
elif [ -f "/Applications/Telegram.app" ]; then
    rm -f "/Applications/Telegram.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
