#!/bin/bash
# Uninstall script for Tenable Nessus Agent
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Tenable Nessus Agent..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Tenable Nessus Agent if running..."
pkill -f "Tenable Nessus Agent" 2>/dev/null || true

# Unload service com.tenablesecurity.nessusagent
echo "Unloading service com.tenablesecurity.nessusagent..."
launchctl unload -w /Library/LaunchAgents/com.tenablesecurity.nessusagent.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.tenablesecurity.nessusagent.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.tenablesecurity.nessusagent.plist 2>/dev/null || true

echo "Uninstallation complete!"
exit 0
