#!/bin/bash
# Uninstall script for Termius
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Termius..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Termius if running..."
pkill -f "Termius" 2>/dev/null || true

# Remove /Applications/Termius.app
echo "Removing /Applications/Termius.app..."
if [ -d "/Applications/Termius.app" ]; then
    rm -rf "/Applications/Termius.app" 2>/dev/null || true
elif [ -f "/Applications/Termius.app" ]; then
    rm -f "/Applications/Termius.app" 2>/dev/null || true
fi

# Remove $HOME/.termius
echo "Removing $HOME/.termius..."
if [ -d "$HOME/.termius" ]; then
    rm -rf "$HOME/.termius" 2>/dev/null || true
elif [ -f "$HOME/.termius" ]; then
    rm -f "$HOME/.termius" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Termius
echo "Removing $HOME/Library/Application Support/Termius..."
if [ -d "$HOME/Library/Application Support/Termius" ]; then
    rm -rf "$HOME/Library/Application Support/Termius" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Termius" ]; then
    rm -f "$HOME/Library/Application Support/Termius" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Termius
echo "Removing $HOME/Library/Logs/Termius..."
if [ -d "$HOME/Library/Logs/Termius" ]; then
    rm -rf "$HOME/Library/Logs/Termius" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Termius" ]; then
    rm -f "$HOME/Library/Logs/Termius" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.termius-dmg.mac.savedState
echo "Removing $HOME/Library/Saved Application State/com.termius-dmg.mac.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.termius-dmg.mac.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.termius-dmg.mac.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.termius-dmg.mac.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.termius-dmg.mac.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
