# Kill application with bundle ID org.thonny.Thonny if running
echo "Stopping application with bundle ID org.thonny.Thonny if running..."
killall -9 "org.thonny.Thonny" 2>/dev/null || true

# Remove $HOME/Library/Saved Application State/org.thonny.Thonny.savedState
echo "Removing $HOME/Library/Saved Application State/org.thonny.Thonny.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.thonny.Thonny.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.thonny.Thonny.savedState" 2>/dev/null || true
