    rm -f "$HOME/Library/Saved Application State/org.thonny.Thonny.savedState" 2>/dev/null || true
fi

# Remove $HOME/Library/Thonny
echo "Removing $HOME/Library/Thonny..."
if [ -d "$HOME/Library/Thonny" ]; then
    rm -rf "$HOME/Library/Thonny" 2>/dev/null || true
