#!/bin/bash
# Uninstall script for Todoist
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Todoist..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Todoist if running..."
pkill -f "Todoist" 2>/dev/null || true

# Remove /Applications/Todoist.app
echo "Removing /Applications/Todoist.app..."
if [ -d "/Applications/Todoist.app" ]; then
    rm -rf "/Applications/Todoist.app" 2>/dev/null || true
elif [ -f "/Applications/Todoist.app" ]; then
    rm -f "/Applications/Todoist.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/*com.todoist.mac*
echo "Removing $HOME/Library/Application Scripts/*com.todoist.mac*..."
if [ -d "$HOME/Library/Application Scripts/*com.todoist.mac*" ]; then
    rm -rf "$HOME/Library/Application Scripts/*com.todoist.mac*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/*com.todoist.mac*" ]; then
    rm -f "$HOME/Library/Application Scripts/*com.todoist.mac*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Todoist
echo "Removing $HOME/Library/Application Support/Todoist..."
if [ -d "$HOME/Library/Application Support/Todoist" ]; then
    rm -rf "$HOME/Library/Application Support/Todoist" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Todoist" ]; then
