fi

# Remove $HOME/Library/Group Containers/*com.todoist.mac*
echo "Removing $HOME/Library/Group Containers/*com.todoist.mac*..."
if [ -d "$HOME/Library/Group Containers/*com.todoist.mac*" ]; then
    rm -rf "$HOME/Library/Group Containers/*com.todoist.mac*" 2>/dev/null || true
elif [ -f "$HOME/Library/Group Containers/*com.todoist.mac*" ]; then
    rm -f "$HOME/Library/Group Containers/*com.todoist.mac*" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Todoist
echo "Removing $HOME/Library/Logs/Todoist..."
if [ -d "$HOME/Library/Logs/Todoist" ]; then
