elif [ -f "$HOME/Library/Logs/Todoist" ]; then
    rm -f "$HOME/Library/Logs/Todoist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.todoist.mac.Todoist.plist
echo "Removing $HOME/Library/Preferences/com.todoist.mac.Todoist.plist..."
if [ -d "$HOME/Library/Preferences/com.todoist.mac.Todoist.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.todoist.mac.Todoist.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.todoist.mac.Todoist.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.todoist.mac.Todoist.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.todoist.mac.Todoist.savedState
echo "Removing $HOME/Library/Saved Application State/com.todoist.mac.Todoist.savedState..."
