#!/bin/bash
# Uninstall script for VeraCrypt
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling VeraCrypt..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping VeraCrypt if running..."
pkill -f "VeraCrypt" 2>/dev/null || true

# Remove $HOME/Library/Application Support/VeraCrypt
echo "Removing $HOME/Library/Application Support/VeraCrypt..."
if [ -d "$HOME/Library/Application Support/VeraCrypt" ]; then
    rm -rf "$HOME/Library/Application Support/VeraCrypt" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/VeraCrypt" ]; then
    rm -f "$HOME/Library/Application Support/VeraCrypt" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.idrix.VeraCrypt.plist
echo "Removing $HOME/Library/Preferences/org.idrix.VeraCrypt.plist..."
if [ -d "$HOME/Library/Preferences/org.idrix.VeraCrypt.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.idrix.VeraCrypt.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.idrix.VeraCrypt.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.idrix.VeraCrypt.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState
echo "Removing $HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.idrix.VeraCrypt.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
