#!/bin/bash
# Uninstall script for VisualVM
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling VisualVM..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping VisualVM if running..."
pkill -f "VisualVM" 2>/dev/null || true

# Remove /Applications/VisualVM.app
echo "Removing /Applications/VisualVM.app..."
if [ -d "/Applications/VisualVM.app" ]; then
    rm -rf "/Applications/VisualVM.app" 2>/dev/null || true
elif [ -f "/Applications/VisualVM.app" ]; then
    rm -f "/Applications/VisualVM.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/VisualVM
echo "Removing $HOME/Library/Application Support/VisualVM..."
if [ -d "$HOME/Library/Application Support/VisualVM" ]; then
    rm -rf "$HOME/Library/Application Support/VisualVM" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/VisualVM" ]; then
    rm -f "$HOME/Library/Application Support/VisualVM" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/VisualVM
echo "Removing $HOME/Library/Caches/VisualVM..."
if [ -d "$HOME/Library/Caches/VisualVM" ]; then
    rm -rf "$HOME/Library/Caches/VisualVM" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/VisualVM" ]; then
    rm -f "$HOME/Library/Caches/VisualVM" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
