#!/bin/bash
# Uninstall script for WhatsApp
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling WhatsApp..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping WhatsApp if running..."
pkill -f "WhatsApp" 2>/dev/null || true

# Remove /Applications/WhatsApp.app
echo "Removing /Applications/WhatsApp.app..."
if [ -d "/Applications/WhatsApp.app" ]; then
    rm -rf "/Applications/WhatsApp.app" 2>/dev/null || true
elif [ -f "/Applications/WhatsApp.app" ]; then
    rm -f "/Applications/WhatsApp.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
