#!/bin/bash
# Uninstall script for WineHQ-stable
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling WineHQ-stable..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping WineHQ-stable if running..."
pkill -f "WineHQ-stable" 2>/dev/null || true

# Remove /Applications/Wine Stable.app
echo "Removing /Applications/Wine Stable.app..."
if [ -d "/Applications/Wine Stable.app" ]; then
    rm -rf "/Applications/Wine Stable.app" 2>/dev/null || true
elif [ -f "/Applications/Wine Stable.app" ]; then
    rm -f "/Applications/Wine Stable.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
