#!/bin/bash
# Uninstall script for XMind
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling XMind..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping XMind if running..."
pkill -f "XMind" 2>/dev/null || true

# Remove /Applications/Xmind.app
echo "Removing /Applications/Xmind.app..."
if [ -d "/Applications/Xmind.app" ]; then
    rm -rf "/Applications/Xmind.app" 2>/dev/null || true
elif [ -f "/Applications/Xmind.app" ]; then
    rm -f "/Applications/Xmind.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.xmind.cathy.application.savedState
echo "Removing $HOME/Library/Saved Application State/org.xmind.cathy.application.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.xmind.cathy.application.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.xmind.cathy.application.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.xmind.cathy.application.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.xmind.cathy.application.savedState" 2>/dev/null || true
fi

# Remove $HOME/Library/XMind
echo "Removing $HOME/Library/XMind..."
if [ -d "$HOME/Library/XMind" ]; then
    rm -rf "$HOME/Library/XMind" 2>/dev/null || true
elif [ -f "$HOME/Library/XMind" ]; then
    rm -f "$HOME/Library/XMind" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
