#!/bin/bash
# Uninstall script for Zenmap
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Zenmap..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Zenmap if running..."
pkill -f "Zenmap" 2>/dev/null || true

# Remove $HOME/.zenmap
echo "Removing $HOME/.zenmap..."
if [ -d "$HOME/.zenmap" ]; then
    rm -rf "$HOME/.zenmap" 2>/dev/null || true
elif [ -f "$HOME/.zenmap" ]; then
    rm -f "$HOME/.zenmap" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.insecure.Zenmap.savedState
echo "Removing $HOME/Library/Saved Application State/org.insecure.Zenmap.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.insecure.Zenmap.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.insecure.Zenmap.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.insecure.Zenmap.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.insecure.Zenmap.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
